package com.task1.day1;

public class Logicaloperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=2;
		
        boolean lessthan=a<b;
        boolean greaterthan=a>b;
        boolean lessthanequalto=a<=b;
        boolean greaterthanequalto=a<=b;
        boolean equalto=a==b;
        System.out.println(" Answer is a<b (T/F) :"+lessthan);
        System.out.println("Answer is a>b (T/F :"+greaterthan);
        System.out.println("Answer is a<=b (T/F :"+lessthanequalto);
        System.out.println("Answer is a>=b (T/F :"+greaterthanequalto);
        System.out.println("Answer is a==b (T/F :"+equalto);
        
        
        
	}

}
